function detectmob(){return window.innerWidth<=465}if(!detectmob()){var modal=document.querySelector(".modal"),modalBg=document.querySelector(".modal-bg"),closeButton=document.querySelector(".close-button"),closeBg=document.querySelector(".modal-bg"),openImg=document.getElementsByClassName("open-img");closeButton.addEventListener("click",function(){modal.classList.toggle("closed"),modalBg.classList.toggle("closed")}),closeBg.addEventListener("click",function(){modal.classList.toggle("closed"),modalBg.classList.toggle("closed")});for(var i=0;i<openImg.length;i++)openImg[i].addEventListener("click",function(e){document.getElementById("modal-content").innerHTML=e.target.parentElement.innerHTML,modal.classList.toggle("closed"),modalBg.classList.toggle("closed")})}